import React from "react";
import pic1 from "../assets/pic.png";

const Details = ({ pic, name, about, isUser }) => {
  return isUser ? (
    <>
      <div className="max-w-[1440px] h-screen bg-black">
        <div className="w-[90%] h-[90%] mx-auto flex justify-center items-center space-x-4">
          {/* profile pic */}
          <div className="w-[45%] h-[50%] rounded-full">
            <img src={pic1} alt="" className="h-full w-full rounded-full" />
          </div>
          {/* details */}
          <div className="flex w-[50%] h-[50%] justify-center items-center">
            <div className="flex flex-col space-y-8 ">
              <h1 className="text-white text-center text-3xl">{name}</h1>
              <p className="text-white text-left px-20">{about}</p>
            </div>
          </div>
        </div>
      </div>
    </>
  ) : (
    <>
      <div className="max-w-[1440px] h-screen bg-black pt-10">
        <div className="w-[90%] h-auto mx-auto flex flex-col space-y-5">
          {/* title */}
          <h1 className="text-white pt-5 text-xl font-bold">
            Lorem ipsum, dolor sit amet consectetur adipisicing elit.
          </h1>
          {/* coverPic */}
          <div className="">
            <img src={pic1} alt="" className="w-[80%] h-[50%] rounded-xl " />
          </div>
          {/* description */}
          <p className="w-[80%] text-white mt-6 text-base font-light ">
            Lorem ipsum, dolor sit amet consectetur adipisicing elit.
            Laudantium, recusandae alias esse totam laborum voluptatibus hic rem
            sed non minima dolorum repudiandae. Similique aliquam laudantium
            dolores eveniet molestiae nesciunt possimus earum mollitia itaque
            eligendi, hic, placeat, iure culpa laborum architecto assumenda
            dolorum qui in pariatur?{about}
          </p>
        </div>
      </div>
    </>
  );
};

export default Details;
