import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const UserDetail = () => {
  const params = useParams();
  const id = params.id;
  const [userDetail, setUserDetail] = useState({
    name: "",
    about: "",
    avatar: "",
  });

  useEffect(() => {
    let ignore = false;
    fetchUserDetails();
    async function fetchUserDetails() {
      const res = await axios.get(
        `https://64cb8f7f700d50e3c7061cf4.mockapi.io/api/users/${id}`
      );
      if (!ignore) {
        setUserDetail(res.data);
      }
      return () => {
        ignore = true;
      };
    }
  }, [id]);

  return (
    <div className="w-screen h-screen bg-black">
      <div className="w-[90%] h-[90%] mx-auto flex justify-center items-center space-x-4">
        {/* profile pic */}
        <div className="w-[45%] h-[60%] rounded-full">
          <img src={""} alt="" className="h-full w-full rounded-full" />
        </div>
        {/* details */}
        <div className="flex w-[50%] h-[50%] justify-center items-center">
          <div className="flex flex-col space-y-8 ">
            <h1 className="text-white text-center text-3xl">name</h1>
            <p className="text-white text-left px-20">
              Lorem ipsum dolor, sit amet consectetur adipisicing elit. Fugit
              debitis laudantium illo. Necessitatibus deleniti modi quasi unde
              velit labore distinctio quaerat nostrum similique!
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDetail;
