import React from "react";
import { Link } from "react-router-dom";

const ListingItem = ({ items, isUser }) => {
  return (
    <div className="w-screen h-auto bg-black">
      <div className="h-auto w-[90%] mx-auto">
        <h1 className="text-2xl px-10 py-5 text-white text-center pt-10">
          <span className="border border-white  px-10 py-5">
            {isUser ? "Users" : "Posts"}
          </span>
        </h1>
        <ol className=" list-disc flex flex-col space-y-4">
          {items.map((item, id) => {
            return (
              <Link to={`/${isUser ? "user" : "post"}/${id + 1}`}>
                <li className="text-white px-5 py-4">{item}</li>
                <hr />
              </Link>
            );
          })}
        </ol>
      </div>
    </div>
  );
};

export default ListingItem;
