import React from "react";
import { Link } from "react-router-dom";

const ListingItem = ({ items, isUser }) => {
  return (
    <div className="w-screen h-auto bg-black border border-black">
      <div className="h-auto w-[80%] mx-auto my-10 ">
        <h1 className="text-xl  lg:text-2xl text-white text-center">
          <span className="relative px-10 py-3 before:w-full before:h-full before:absolute before:top-0 before:left-0 before:border before:border-white before:hover:origin-center before:hover:rotate-180 before:transition before:duration-700">
            {isUser ? "Users" : "Posts"}
          </span>
        </h1>
        <ul className=" list-disc flex flex-col space-y-2 my-10">
          {items.map((item, id) => {
            return (
              <Link
                to={`/${isUser ? "user" : "post"}/${id + 1}`}
                className="w-[70%]"
              >
                <li className="text-white px-5 py-2 text-md md:text-xl relative transition duration-1000 before:absolute before:top-0 before:left-0 before:bg-white before:w-full before:h-0 before:transition before:duration-1000 hover:text-black before::hover:h-full  ">
                  {item}
                </li>
                <hr className="" />
              </Link>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default ListingItem;
